package com.dkd.common.web;import org.junit.Test;import org.springframework.mock.web.MockHttpServletRequest;import org.springframework.mock.web.MockHttpServletResponse;import org.springframework.mock.web.MockHttpSession;public class BaseControllerTest {    @Test    public void test() {        MockHttpServletRequest request = new MockHttpServletRequest();        request.setMethod("GET");        request.addHeader("Origin","*");        MockHttpServletResponse response = new MockHttpServletResponse();        BaseController baseController = new BaseController();        baseController.getPageNum(null,null);        baseController.getPageNum("0","10");        baseController.getPageNum("1","10");        baseController.getPageSize(null);        baseController.getPageSize("10");        baseController.getDraw("10");        baseController.getDraw(null);        baseController.setHttpServletResponse(response);        baseController.setHttpServletResponse(response,request);        baseController.checkWebUserLogin(request);        baseController.deleteSession(request);    }    @Test    public void test1() {        BaseController baseController = new BaseController();        MockHttpServletRequest request = new MockHttpServletRequest();        MockHttpSession mockHttpSession = new MockHttpSession();        mockHttpSession.setAttribute("userId",1000);        request.setSession(mockHttpSession);        baseController.checkWebUserLogin(request);    }}