package com.dkd.common.web;import com.dkd.model.XydManageAccount;import org.junit.Test;import org.springframework.mock.web.MockHttpServletRequest;import org.springframework.mock.web.MockHttpServletResponse;import org.springframework.mock.web.MockHttpSession;import org.springframework.web.servlet.ModelAndView;public class LoginContextInterceptorTest {    private MockHttpServletRequest mockHttpServletRequest =new MockHttpServletRequest();    private MockHttpServletResponse mockHttpServletResponse = new MockHttpServletResponse();    LoginContextInterceptor loginContextInterceptor = new LoginContextInterceptor();    @Test    public void preHandle() throws Exception{        loginContextInterceptor.preHandle(mockHttpServletRequest,mockHttpServletResponse,new Object());        mockHttpServletRequest.setRequestURI("/user/login");        loginContextInterceptor.preHandle(mockHttpServletRequest,mockHttpServletResponse,new Object());        mockHttpServletRequest.setRequestURI("/user/toLogin");        loginContextInterceptor.preHandle(mockHttpServletRequest,mockHttpServletResponse,new Object());        MockHttpSession mockHttpSession = new MockHttpSession();        mockHttpSession.setAttribute("user",new XydManageAccount());        mockHttpServletRequest = new MockHttpServletRequest();        mockHttpServletRequest.setSession(mockHttpSession);        loginContextInterceptor.preHandle(mockHttpServletRequest,mockHttpServletResponse,new Object());    }    @Test    public void postHandle() throws Exception{        loginContextInterceptor.postHandle(mockHttpServletRequest,mockHttpServletResponse,new Object(),new ModelAndView());    }    @Test    public void afterCompletion()throws Exception {        loginContextInterceptor.afterCompletion(mockHttpServletRequest,mockHttpServletResponse,new Object(),new RuntimeException());    }}