package com.dkd.manage;import com.dkd.XiaoyudiApplication;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;import org.springframework.test.web.servlet.MockMvc;import org.springframework.test.web.servlet.MvcResult;import org.springframework.test.web.servlet.ResultActions;import org.springframework.test.web.servlet.request.MockMvcRequestBuilders;import org.springframework.test.web.servlet.setup.MockMvcBuilders;import org.springframework.transaction.annotation.Transactional;@RunWith(SpringJUnit4ClassRunner.class)@SpringBootTest(classes = XiaoyudiApplication.class, properties = "/application.properties")@AutoConfigureMockMvc@Transactionalpublic class UserStudyControllerTest {    private final Logger logger = LoggerFactory.getLogger(UserStudyControllerTest.class);    private MockMvc mockMvc;    @Autowired    private UserStudyController userStudyController;    private String reqUrl = "/manage/study";    @Before    public void setUp() throws Exception {        this.mockMvc = MockMvcBuilders.standaloneSetup(userStudyController).build();    }    @After    public void tearDown() throws Exception {    }    @Test    public void toUserStudyInfo() throws Exception {        ResultActions resultActions = this.mockMvc.perform(MockMvcRequestBuilders                .post(reqUrl + "/toUserStudyInfo")                .param("id", "126")                );        MvcResult mvcResult = resultActions.andReturn();        logger.info("=====toUserStudyInfo mvcResult:" + mvcResult.getResponse().getStatus());        String result = mvcResult.getResponse().getContentAsString();        logger.info("=====toUserStudyInfo result:" + result);    }    @Test    public void getUserDataInfo() throws Exception {        ResultActions resultActions = this.mockMvc.perform(MockMvcRequestBuilders.post(reqUrl + "/getUserDataInfo")                .param("id", "126")                );        MvcResult mvcResult = resultActions.andReturn();        logger.info("=====getUserDataInfo mvcResult:" + mvcResult.getResponse().getStatus());        String result = mvcResult.getResponse().getContentAsString();        logger.info("=====getUserDataInfo result:" + result);    }    @Test    public void getUserStudyInfo() throws Exception {        ResultActions resultActions = this.mockMvc.perform(MockMvcRequestBuilders.post(reqUrl + "/getUserStudyInfo")                .param("id", "126")                .param("timeType", "2")        );        MvcResult mvcResult = resultActions.andReturn();        logger.info("=====generalLogin mvcResult:" + mvcResult.getResponse().getStatus());        String result = mvcResult.getResponse().getContentAsString();        logger.info("=====generalLogin result:" + result);    }}